        try {
            const res = await fetch("https://fakestoreapi.com/products")
            const actualData = await res.json();
            for (let index=0; index<=3;index++){
                setData(actualData[2])

            }


        } catch (err) {
            console.log(err);

        }

    }
    useEffect(() => {
        getProductData();
    }, [])
    return (
        <>
            <h1 className='heading_style'>LifeStyle Products</h1>
            <ul>
                <li className='card'>

                    <div className='card__main'>
                        <img src={data.image} alt="myPic" className='card__img' /><br></br>
                        <div className='card__info'>
                            <h2 className='card__name'>Laptop Bag</h2><br></br>

                            <p className='card__price'>Price: {data.price}</p><br></br>
                            <span className='card__category'>{data.description} </span>
                            <h3 className='card__title'>{data.title}</h3>
                            <p className='card__category'>{data.category}</p><br></br>
                        </div>
                    </div>
                </li>
                <li className='card'>

                    <div className='card__main'>
