import React, { useEffect, useState } from 'react'

const Products=()=>{

    const[data,setData]= useState([]);
    const getProductData=async()=>{

        try{
            const res= await fetch("https://fakestoreapi.com/products")
            const actualData=await res.json();
            console.log(actualData[0])
            setData(actualData[0])

        }catch(err){
            console.log(err);

        }

    }
    useEffect(()=>{
        getProductData();
    },[])
  return(
      <>
      <h1 className='heading_style'>LifeStyle Products</h1>
      <ul>
          <li className='card'>
              <div className='card__main'>
              <img src={data.imgsrc} alt="myPic" className='card__img' />
                  <div className='card__inner'>
                      <p className='card__name'><span>Men's</span> Clothing</p>
                      <p className='card__total card__small'>{data.price}</p>
                  </div>
              </div>
          </li>
          <li className='card'>
              <div className='card__main'>
                  <div className='card__inner'>
                      <p className='card__name'><span>Women</span> Clothing</p>
                      <p className='card__total card__small'>Price</p>
                  </div>
              </div>
          </li>
          <li className='card'>
              <div className='card__main'>
                  <div className='card__inner'>
                      <p className='card__name'><span>Kids</span> Clothing</p>
                      <p className='card__total card__small'>Price</p>
                  </div>
              </div>
          </li>
      </ul>
      </>

  )
}

export default Products
